// Сортировка слиянием (Merge Sort)
// Рекурсивный алгоритм "разделяй и властвуй"
//
// ============ КРАТКАЯ СПРАВКА ============
//
// ОСНОВНАЯ ФУНКЦИЯ:
//   void MergeSort(int* arr, int size) - отсортировать массив
//
// АЛГОРИТМ:
//   1. Если последовательность содержит только один элемент - возвращается
//   2. Иначе разбивается на две примерно равные части
//   3. Каждая часть рекурсивно сортируется
//   4. Отсортированные части сливаются в порядке возрастания
//   5. Получившийся отсортированный массив возвращается
//
// ==========================================

#include <stdio.h>
#include <stdlib.h>

// Слияние двух отсортированных массивов в один
void Merge(int* arr_1, int* arr_2, int* arr, int size_1, int size_2) {
    int i = 0;  // Индекс первого массива
    int j = 0;  // Индекс второго массива
    int p = 0;  // Индекс результирующего массива

    // Сливаем элементы из обоих массивов в порядке возрастания
    while (i < size_1 && j < size_2) {
        if (arr_1[i] <= arr_2[j]) {
            arr[p++] = arr_1[i++];
        } else {
            arr[p++] = arr_2[j++];
        }
    }

    // Копируем оставшиеся элементы из первого массива
    while (i < size_1) {
        arr[p++] = arr_1[i++];
    }

    // Копируем оставшиеся элементы из второго массива
    while (j < size_2) {
        arr[p++] = arr_2[j++];
    }
}

// Рекурсивная сортировка слиянием
void MergeSort(int* arr, int size) {
    // Базовый случай: массив из одного элемента уже отсортирован
    if (size == 1) {
        return;
    }

    // Создаем левую половину массива
    int* arr_1 = (int*)malloc((size / 2) * sizeof(int));
    for (int i = 0; i < size / 2; i++) {
        arr_1[i] = arr[i];
    }

    // Создаем правую половину массива (может быть на 1 элемент больше)
    int* arr_2 = (int*)malloc((size / 2 + size % 2) * sizeof(int));
    for (int i = 0; i < size / 2 + size % 2; i++) {
        arr_2[i] = arr[i + size / 2];
    }

    // Рекурсивно сортируем обе половины
    MergeSort(arr_1, size / 2);
    MergeSort(arr_2, size / 2 + size % 2);

    // Сливаем отсортированные половины обратно в исходный массив
    Merge(arr_1, arr_2, arr, size / 2, size / 2 + size % 2);

    // Освобождаем временные массивы
    free(arr_1);
    free(arr_2);
}

// ============ СЛОЖНОСТЬ ============
// Время: O(n log n) во всех случаях
// Память: O(n log n) для временных массивов на всех уровнях рекурсии
// Стабильная: Да (сохраняет порядок равных элементов)

// Особенности:
// - Гарантированная сложность O(n log n)
// - Стабильная сортировка
// - Простая и понятная реализация
// - Требует дополнительную память
// - Хорошо подходит для связных списков
// - Легко распараллеливается

